// global
let ADMIN = false;

function storeToDb(movie) {
    let openRequest = indexedDB.open("db", 2);

    openRequest.onsuccess = function () {
        let db = openRequest.result;
        let transaction = db.transaction("movies", "readwrite"); // (1)

        let movies = transaction.objectStore('movies');

        let request = movies.add(movie);
        setTimeout(() => {
        }, 200)
        request.onsuccess = () => {
            setTimeout(() => {
                window.open('./index.html', '_self')
            }, 200)
        };
    };
}

function removeFromDb(name, navigate) {
    let openRequest = indexedDB.open("db", 2);

    openRequest.onsuccess = function () {
        let db = openRequest.result;
        let transaction = db.transaction("movies", "readwrite"); // (1)

        let movies = transaction.objectStore('movies');

        let request = movies.delete(name);
        setTimeout(() => {
        }, 200)
        request.onsuccess = () => {
            setTimeout(() => {
                if(navigate){
                    window.open('./index.html', '_self')
                }
            }, 200)
        };
    };
}

function openNewPage() {
    window.open('./new.html', '_self');
}

// new.html
function save() {
    const name = document.getElementById('name').value;
    const image = document.getElementById('uploadImage').value
    const description = document.getElementById('description').value;
    if (!name || name.trim().length === 0) {
        alert('Некорректное "Название"')
        return
    }
    if (!image || image.trim() === 'data:,') {
        alert('Картинка на Превью обязательна')
        return
    }
    if (!description || description.trim().length === 0) {
        alert('Некорректное "Описание"')
        return
    }
    const movie = {
        name: document.getElementById('name').value,
        image: document.getElementById('uploadImage').value,
        trailerLink: document.getElementById('trailerLink').value.replace('watch?v=', 'embed/'),
        description: document.getElementById('description').value,
        score: document.getElementById('score').value
    }

    let c = document.createElement('canvas');
    let img = document.getElementById('uploadPreview');
    c.height = img.naturalHeight;
    c.width = img.naturalWidth;
    let ctx = c.getContext('2d');
    ctx.drawImage(img, 0, 0, c.width, c.height);

    movie.image = c.toDataURL();
    storeToDb(movie)
}

function edit() {
    const name = new URLSearchParams(window.location.search).get('name');
    removeFromDb(name, false);
    save();
}

function previewImage() {
    var oFReader = new FileReader();
    oFReader.readAsDataURL(document.getElementById("uploadImage").files[0]);

    oFReader.onload = function (oFREvent) {
        document.getElementById("uploadPreview").removeAttribute('hidden')
        document.getElementById("uploadPreview").src = oFREvent.target.result;
    };
}

// search
let timeout = undefined;

function search(event) {
    const value = event.target.value.trim();
    clearTimeout(timeout);
    timeout = setTimeout(() => {
        showFilteredData(value)
    }, 1000)
}

function showFilteredData(value) {
    const movieList = document.querySelector('#movie-list');
    movieList.innerHTML = '';
    let openRequest = indexedDB.open("db", 2);

    openRequest.onupgradeneeded = function () {
        let db = openRequest.result;
        if (!db.objectStoreNames.contains('movies')) { // если хранилище "books" не существует
            db.createObjectStore('movies', {keyPath: 'name'}); // создаём хранилище
        }
    }
    openRequest.onsuccess = function () {
        let db = openRequest.result;
        let transaction = db.transaction("movies", "readwrite"); // (1)

        let getAll = transaction.objectStore('movies').getAll()
        setTimeout(() => {
        }, 200)
        getAll.onsuccess = () => {
            getAll.result.filter(m => m.name.toLowerCase().includes(value.toLowerCase())).forEach((movie, index) => createMovieContainer(movie, index));
        };
    };
}

