function load() {
    if(window.location.href.includes('edit') || window.location.href.includes('edit.html')){
        const name = new URLSearchParams(window.location.search).get('name');

        let openRequest = indexedDB.open("db", 2);

        openRequest.onupgradeneeded = function () {
            let db = openRequest.result;
            if (!db.objectStoreNames.contains('movies')) { // если хранилище "books" не существует
                db.createObjectStore('movies', {keyPath: 'name'}); // создаём хранилище
            }
        }
        openRequest.onsuccess = function () {
            let db = openRequest.result;
            let transaction = db.transaction("movies", "readwrite"); // (1)

            let getOne = transaction.objectStore('movies').get(name)
            setTimeout(() => {
            }, 200)
            getOne.onsuccess = () => {
                const movie = getOne.result;
                const myFile = new File([movie.image], 'image.'+movie.image.split(';base64')[0].replace('data:','').split('/')[1], {
                    type: movie.image.split(';base64')[0].replace('data:',''),
                });
                document.getElementById('name').value = movie.name;
                const dataTransfer = new DataTransfer();
                dataTransfer.items.add(myFile);
                document.getElementById('uploadImage').files = dataTransfer.files;
                document.getElementById("uploadPreview").src = movie.image;
                document.getElementById("uploadPreview").hidden = false;
                document.getElementById("trailerLink").value = movie.trailerLink;
                document.getElementById('description').value = movie.description;
                document.getElementById('score').value = movie.score;
            };
        };
    }
    localStorage.setItem('modalOpen', 'false');
    if (!localStorage.getItem('populated') || localStorage.getItem('populated') === 'false') {
        populate();
        localStorage.setItem('populated', 'true');
    }
    showData()
}

