function showData() {
    let openRequest = indexedDB.open("db", 2);

    openRequest.onupgradeneeded = function () {
        let db = openRequest.result;
        if (!db.objectStoreNames.contains('movies')) { // если хранилище "books" не существует
            db.createObjectStore('movies', {keyPath: 'name'}); // создаём хранилище
        }
    }
    openRequest.onsuccess = function () {
        let db = openRequest.result;
        let transaction = db.transaction("movies", "readwrite"); // (1)

        let getAll = transaction.objectStore('movies').getAll()
        setTimeout(() => {
        }, 200)
        getAll.onsuccess = () => {
            getAll.result.forEach((movie, index) => createMovieContainer(movie, index));
        };
    };
}

function createMovieContainer(movie, index) {
    const movieList = document.querySelector('#movie-list');

    const movieItem = document.createElement('li');
    movieItem.classList.add('movie-item', 'd-flex', 'justify-content-center', 'align-items-start', 'flex-column', 'm-2', 'bg-black', 'text-white', 'rounded-3', 'shadow');
    movieItem.setAttribute('data-movie', movie);
    movieItem.id = 'movie' + index;
    movieItem.onclick = (event) => {
        event.stopPropagation();
        event.preventDefault();

        showMovieModal(movie);
    }

    const movieImage = document.createElement('span');
    movieImage.classList.add('movie-image', 'd-block', 'rounded-2', 'cursor-pointer');
    movieImage.style.height = '175px';
    movieImage.style.width = '300px';
    movieImage.style.background = 'url(' + (movie.image === 'data:,' ? './images/kinosearch.png' : movie.image) + ') no-repeat center';
    movieImage.style.backgroundSize = 'cover';
    movieImage.style.backgroundPosition = 'top';

    const movieTitle = document.createElement('span');
    movieTitle.classList.add('movie-title', 'w-100', 'px-1', 'd-flex');

    const movieName = document.createElement('marquee');
    movieName.textContent = movie.name;
    movieName.classList.add('w-75', 'fw-bold');

    const movieScore = document.createElement('span');
    movieScore.textContent = movie.score + '⭐';
    movieScore.classList.add('w-25', 'd-inline-block', 'text-end');

    movieTitle.appendChild(movieName);
    movieTitle.appendChild(movieScore);

    movieItem.appendChild(movieImage);
    movieItem.appendChild(movieTitle);

    if (movieList) {
        movieList.appendChild(movieItem);
    }

}

function showMovieModal(movie) {
    localStorage.setItem('modalOpen', 'true');
    const modal = document.getElementById('movieModal');
    modal.style.top = '12.5%';

    const movieImage = document.getElementById('movieImage');
    movieImage.style.background = 'url(' + (movie.image === 'data:,' ? './images/kinosearch.png' : movie.image) + ') no-repeat center';
    movieImage.style.backgroundSize = 'cover';
    movieImage.style.backgroundPosition = 'top';
    movieImage.style.height = '195px';
    movieImage.style.width = '340px';

    document.getElementById('movieName').textContent = movie.name;
    document.getElementById('movieRemoveName').dataset.name = movie.name;
    document.getElementById('movieEditName').dataset.name = movie.name;

    document.getElementById('movieStar').innerHTML = '<span>⭐</span>'.repeat(movie.score) + '<span id="movieScore" class="text-white p-1">' + movie.score + '</span>';
    document.getElementById('movieDescription').textContent = movie.description;

    const movieTrailer = document.getElementById('movieTrailer')
    movieTrailer.src = movie.trailerLink;

}

function closeModal() {
    localStorage.setItem('modalOpen', 'false');
    const modal = document.getElementById('movieModal');
    modal.style.top = '-200%';
    document.getElementById('movieTrailer').src = '';
}

function removeMovie(event) {
    if (confirm('Хотите удалить даное кино?')) {
        let name = event.target.dataset.name;
        removeFromDb(name, true);
    }
}

function editMovie(event) {
    if (confirm('Хотите изменить данное кино?')) {
        let name = event.target.dataset.name;
        window.open('./edit.html?name='+name,'_self',)
    }
}

function random() {
    if (localStorage.getItem('modalOpen') && localStorage.getItem('modalOpen') === 'true') {
        return;
    }
    let openRequest = indexedDB.open("db", 2);

    openRequest.onupgradeneeded = function () {
        let db = openRequest.result;
        if (!db.objectStoreNames.contains('movies')) { // если хранилище "books" не существует
            db.createObjectStore('movies', {keyPath: 'name'}); // создаём хранилище
        }
    }
    openRequest.onsuccess = function () {
        let db = openRequest.result;
        let transaction = db.transaction("movies", "readwrite"); // (1)

        let getAll = transaction.objectStore('movies').getAll()
        setTimeout(() => {
        }, 200)
        getAll.onsuccess = () => {
            const movies = getAll.result;
            showMovieModal(movies[Math.floor(Math.random() * movies.length)])
        };
    };
}

function login(){
    if(document.getElementById('login').value === 'admin' && document.getElementById('password').value === 'admin'){
        localStorage.setItem('admin', 'true');
        window.open('./index.html', '_self')
        return;
    }
    alert('Введены неверные данные');
}

function logout(){
    if(confirm('Хотите выйти из аккаунта Администратора?')){
        localStorage.setItem('admin', 'false');
        window.open('./index.html', '_self')
    }
}
